from functools import wraps

MODULES = []
LIBRARIES = []

def scraper(name, image=None, enabled=True):
    def _decorator(fn):
        MODULES.append({"name": name, "image": image, "view": fn.__name__, "enabled": enabled})
        return fn
    return _decorator


def ungenerate(fn):
    @wraps(fn)
    def _fn(*args, **kwargs):
        return list(fn(*args, **kwargs))
    return _fn

def library(name, enabled=True):
    def _decorator(fn):
        LIBRARIES.append({"name": name, "view": fn.__name__, "enabled": enabled})
        return fn
    return _decorator
